import logo from './logo.svg';
import './App.css';
import {useState} from 'react';

function App() {
  const [prompt, setPrompt] = useState("")
  const [link, setLink] = useState("")
  const getImg = (prompt) => {
    fetch("https://localhost:8080/create", {
      method: "POST",
      body: {
        prompt
      }
    }).then(res => {
      setLink(res.body.data[0].url)
    })
  }
  return (
    <div className="App">
      <input type='submit' onChange={e => setPrompt(e.target.value)}>Enter prompt</input>
      <button onClick={() => setPrompt()}>Submit</button>
    </div>
  );
}

export default App;
